(4363, 308, 'faq_0_items_0_title', 'How can I order a portrait?'),
(4364, 308, '_faq_0_items_0_title', 'field_5fb4d0b482849'),
(4365, 308, 'faq_0_items_0_text', '<p class=\"faq__description\">To simplify the process of ordering, we created 3 simple steps that will help\r\nyou to become a happy owner of handmade portrait:</p>\r\n\r\n<ul class=\"faq__description-list\">\r\n 	<li class=\"faq__description-item\">⦁ Choose the portrait characteristic (number of subjects, painting\r\ntechniques, size, etc.)</li>\r\n 	<li class=\"faq__description-item\">⦁ Upload a photo</li>\r\n 	<li class=\"faq__description-item\">⦁ Approve your order</li>\r\n</ul>\r\n<p class=\"faq__description\">To see more detailed instructions, go to the Procedure page.</p>'),
(4366, 308, '_faq_0_items_0_text', 'field_5fb4d0bd8284a'),
(4367, 308, 'faq_0_items_1_title', 'What medium do you use to create a portrait?'),
(4368, 308, '_faq_0_items_1_title', 'field_5fb4d0b482849'),
(4369, 308, 'faq_0_items_1_text', '<p class=\"faq__description\">We have two kinds of portraits:</p>\r\n\r\n<ul class=\"faq__description-list\">\r\n 	<li class=\"faq__description-item\">⦁ a charcoal portrait is painted on a high-quality thick paper with\r\nhighly intensive charcoal pencils;</li>\r\n 	<li class=\"faq__description-item\">⦁ an oil portrait is created on solid canvas with durable and intense\r\ncolors and doesn’t need any protective glass.</li>\r\n</ul>'),
(4370, 308, '_faq_0_items_1_text', 'field_5fb4d0bd8284a'),
(4371, 308, 'faq_0_items_2_title', 'What should I know to receive the portrait?'),
(4372, 308, '_faq_0_items_2_title', 'field_5fb4d0b482849'),
(4373, 308, 'faq_0_items_2_text', '<p class=\"faq__description\">When you order a portrait, we give you a tracking code. It’s assigned to track\r\nyour package and then pick it up at the post office.</p>'),
(4374, 308, '_faq_0_items_2_text', 'field_5fb4d0bd8284a'),
(4375, 308, 'faq_0_items', '3'),
