global $wp_query;
global $wp;

$current_lang = pll_current_language();
$data['our_gallery_settings'] = get_field('our_gallery_settings_' . $current_lang, 'option');

$data['posts'] = [];

$paged = get_query_var('paged');
if (!$paged) {
    $paged = 1;
}
$args = array(
    'tax_query' => array(),
    'post_type' => 'gallery',
    //'posts_per_page' => 7,
    'paged' => $paged
);
if (isset($_REQUEST['gallery_subject']) && $_REQUEST['gallery_subject']) {
    $args['tax_query'][] = array(
        'taxonomy' => 'gallery_subject',
        'field'    => 'slug',
        'terms'    => trim($_REQUEST['gallery_subject']),
    );
}
if (isset($_REQUEST['gallery_style']) && $_REQUEST['gallery_style']) {
    $args['tax_query'][] = array(
        'taxonomy' => 'gallery_style',
        'field'    => 'slug',
        'terms'    => trim($_REQUEST['gallery_style']),
    );
}
if (isset($_REQUEST['gallery_subject']) && $_REQUEST['gallery_subject'] && isset($_REQUEST['gallery_style']) && $_REQUEST['gallery_style']) {
    $args['tax_query'] = array_merge(['relation' => 'AND'], $args['tax_query']);
}
query_posts( $args );
if ( have_posts() ) {
    while (have_posts()) {
        the_post();
        $data['posts'][] = $post;
    }
}
$args = array(
    'show_all'           => false,
    'current'            => $paged,
    'end_size'           => 1,
    'mid_size'           => 1,
    'prev_next'          => true,
    'prev_text'          => pll__('Prev'),
    'next_text'          => pll__('Next'),
    'add_args'           => true,
    'type'               => 'array',
);
$pagination = paginate_links( $args );
$data['posts_count'] = empty($data['posts']) ? 0 : count($data['posts']);

$data['filters'] = [
    'active_filters' => [
        'subject' => (isset($_REQUEST['gallery_subject']) && $_REQUEST['gallery_subject'] ? trim($_REQUEST['gallery_subject']) : ''),
        'style' => (isset($_REQUEST['gallery_style']) && $_REQUEST['gallery_style'] ? trim($_REQUEST['gallery_style']) : ''),
    ],
    'subjects' => [
        'active' => (isset($_REQUEST['gallery_subject']) && $_REQUEST['gallery_subject'] ? trim($_REQUEST['gallery_subject']) : ''),
        'items' => get_terms( 'gallery_subject', [
            'hide_empty' => false,
            'orderby' => 'name',
        ]),
    ],
    'styles' => [
        'active' => (isset($_REQUEST['gallery_style']) && $_REQUEST['gallery_style'] ? trim($_REQUEST['gallery_style']) : ''),
        'items' => get_terms( 'gallery_style', [
            'hide_empty' => false,
            'orderby' => 'name',
        ]),
    ]
];

get_header();
